import { Injectable } from "@angular/core";
import { jsPDF } from 'jspdf'
import autoTable, { RowInput } from "jspdf-autotable";

@Injectable({
    providedIn: 'root'
})
export class ImpresionService {

    constructor(){}

    //----IMPRIMIR PROFORMA----
    imprimir( encabezado : string[], cuerpo : Array<any>, paciente: any , total: number, saldo: number ,guardar?: boolean){
    // imprimir( encabezado : string[], cuerpo : Array<any>, titulo : string, paciente: any , total: number, saldo: number ,guardar?: boolean){
        const doc = new jsPDF({
            orientation: "portrait",
            unit: "px",
            format: "letter"
        });

        //---Cargar Imagen en Base64---
        const imgData = 'data:image/png;base64,'; // Coloca tu imagen en base64 aquí

        //---Dibujar la imagen en la posición deseada---
        doc.addImage(imgData, 'PNG', doc.internal.pageSize.width - 150, 20, 100, 40);
        //---PARA EL TITULO---

        //---PARA LOS DATOS DEL PACIENTE---
        doc.setFontSize(12);
        doc.text(`Paciente: ${paciente.nombre}`, 35, 70);
        doc.text(`Edad: ${paciente.edad}`, 35, 85);
        doc.text(paciente.tratamiento, 35, 100); // Línea adicional
        doc.text(`Fecha: ${paciente.fecha}`, doc.internal.pageSize.width - 200, 70);
        doc.text(`Fecha de Nac.: ${paciente.fechaNacimiento}`, doc.internal.pageSize.width - 200, 85);

        const startY = 120;

        autoTable(doc, {
            // head: [['Name', 'Email', 'Country']],
            startY: startY,
            head: [encabezado],
            body: cuerpo,
            theme: 'plain', // Quitar colores de fondo y bordes
            didDrawCell: (data) => {
                if (data.section === 'body') {
                  // Dibujar una línea horizontal debajo de cada fila
                  const lineStart = data.cell.x;
                  const lineEnd = data.cell.x + data.cell.width;
                  const lineY = data.cell.y + data.cell.height;
                  doc.setDrawColor(0); // Color negro
                  doc.setLineWidth(0.5); // Grosor de la línea
                  doc.line(lineStart, lineY, lineEnd, lineY); // Línea horizontal debajo de cada celda
                }
            }
        });
        //---AGREGAR TOTAL Y SALDO---
         //---AGREGAR TOTAL Y SALDO---
        const finalY = (doc as any).lastAutoTable.finalY || startY + cuerpo.length * 20;
        doc.setFontSize(12);
        doc.text(`Total = ${total} Bs    Saldo = ${saldo} Bs`, doc.internal.pageSize.width / 2, finalY + 20, { align: 'center' });

        if(guardar){
            const hoy = new Date();
            doc.save(hoy.getDate() + hoy.getMonth() + hoy.getFullYear() + hoy.getTime()+'.pdf');
        } else{
            // Abre la ventana de impresión
            doc.autoPrint();
            window.open(doc.output('bloburl'), '_blank');
        }
    }
    //------HASTA AQUI---------

    //-----IMPRIMIR REPORTE_DE_PAGOS-----
    imprimirReportPagos( encabezado : string[], cuerpo : Array<any>, titulo: string ,guardar?:boolean)
    {
        const doc = new jsPDF({
            orientation: "portrait",
            unit: "px",
            format: "letter"
        });

        doc.text( titulo, doc.internal.pageSize.width / 2, 25, { align: 'center' });
        // autoTable(doc,{
        //     head : [encabezado],
        //     body : [cuerpo]
        // })
         // Asegúrate de que el cuerpo esté formateado correctamente como RowInput[]
        const cuerpoFormateado: RowInput[] = cuerpo.map(item => Object.values(item));

        autoTable(doc, {
            head: [encabezado],
            body: cuerpoFormateado,
        });
        if(guardar){
            const hoy = new Date();
            doc.save(hoy.getDate() + hoy.getMonth() + hoy.getFullYear() + hoy.getTime()+'.pdf');
        } else{
            // Abre la ventana de impresión
            doc.autoPrint();
            window.open(doc.output('bloburl'), '_blank');
        }
    }
    //------HASTA AQUI---------

        //-----IMPRIMIR REPORTE_DE_ESTADOS-----
    imprimirReportEstado( encabezado : string[], cuerpo : Array<any>, titulo: string ,guardar?:boolean)
    {
        const doc = new jsPDF({
            orientation: "portrait",
            unit: "px",
            format: "letter"
        });

        doc.text( titulo, doc.internal.pageSize.width / 2, 25, { align: 'center' });
        // autoTable(doc,{
        //     head : [encabezado],
        //     body : [cuerpo]
        // })
         // Asegúrate de que el cuerpo esté formateado correctamente como RowInput[]
        const cuerpoFormateado: RowInput[] = cuerpo.map(item => Object.values(item));

        autoTable(doc, {
            head: [encabezado],
            body: cuerpoFormateado,
        });
        if(guardar){
            const hoy = new Date();
            doc.save(hoy.getDate() + hoy.getMonth() + hoy.getFullYear() + hoy.getTime()+'.pdf');
        } else{
            // Abre la ventana de impresión
            doc.autoPrint();
            window.open(doc.output('bloburl'), '_blank');
        }
    }
    //------HASTA AQUI---------

    //doc.text( titulo ,doc.internal.pageSize.width /2, 25, {align: 'center'});
    
}